sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;

	c.clicked = function(){
		c.data.action = "getFacts";
		c.server.update();
	}
}

sp_widget.css:
#factsContainer{
  width: 80%;
  margin:auto;
}

#widgetContainer{
  width:100%;
  background-image : url(cat-image.jpg);
  background-position: center;
}

sp_widget.script:
(function(){

	data.facts = [];

	if(input && input.action == "getFacts"){
		try { 
 var r = new sn_ws.RESTMessageV2('x_94557_loaner_req.Cat Facts', 'Default GET');

 var response = r.execute();
 var responseBody = JSON.parse(response.getBody());
 var httpStatus = response.getStatusCode();
			responseBody.all.forEach(function(elem){
				data.facts.push(elem.text);
			});
}
catch(ex) {
 var message = ex.message;
}
	}


})();
